                    _ProcessingText = value;
                    NPC(nameof(ProcessingText));
                    NPC(nameof(IsProcessing));
                }
            }
        }

        public bool IsProcessing => !string.IsNullOrEmpty(ProcessingText);

        private CancellationTokenSource _CTS;
        public CancellationTokenSource CTS
        {
            get => _CTS;
            private set
            {
                if (_CTS != value)
                {
                    _CTS = value;
                    NPC(nameof(CTS));
                    NPC(nameof(CT));
                    NPC(nameof(IsCancellable));
                    NPC(nameof(IsCancelling));
                }
            }
        }

        public CancellationToken CT => CTS?.Token ?? CancellationToken.None;
        public bool IsCancellable => CTS != null;
        public bool IsCancelling => CTS?.IsCancellationRequested == true;
        public DelegateCommand<object> CancelAsync => new(_ => { CTS?.Cancel(); NPC(nameof(IsCancelling)); });

        private bool _IsSearching;
        public bool IsSearching
        {
            get => _IsSearching;
            set
            {
                if (_IsSearching != value)
                {
                    _IsSearching = value;
                    NPC(nameof(IsSearching));
                    NPC(nameof(CanCommitSearch));
                }
            }
        }

        private string _SearchQuery;
        public string SearchQuery
        {
            get => _SearchQuery;
            set
            {
                if (_SearchQuery != value)
                {
                    _SearchQuery = value;
                    NPC(nameof(SearchQuery));
                    NPC(nameof(CanCommitSearch));
                }
            }
        }

        public bool CanCommitSearch => !string.IsNullOrEmpty(SearchQuery) && !IsSearching && (SearchResult == null || SearchQuery != SearchResult.Query || SearchResult.PageNumber != 1);

        private SearchResult _SearchResult;
        public SearchResult SearchResult
        {
            get => _SearchResult;
            set
            {
                if (_SearchResult != value)
                {
                    _SearchResult = value;
                    CacheSearchResult(SearchResult);
                    NPC(nameof(SearchResult));
                    NPC(nameof(HasSearchResult));
                    NPC(nameof(CanCommitSearch));
                }
            }
        }

        public bool HasSearchResult => SearchResult != null;

        public StorySearcher(MainViewModel MVM)
        {
            this.MVM = MVM;
            SearchQuery = "";

#if DEBUG
            SearchQuery = "ass";
            SearchResult = new(this, SearchQuery, 1, GeneralUtils.DeserializeJson<LiteroticaSearchResults>(SampleQueryResponse)); // https://search.literotica.com/?query=ass
#endif
        }

        public DelegateCommand<object> CommitSearch => new(_ =>
        {
            if (CanCommitSearch)
                _ = SearchAsync(SearchQuery, 1);
        });

        private readonly Dictionary<string, SearchResult> RecentSearches = new();
        private static string GetUniqueSearchId(string Query, int PageNumber) => $"{PageNumber}|{Query}";
        private void CacheSearchResult(SearchResult Value) => RecentSearches[GetUniqueSearchId(Value.Query, Value.PageNumber)] = Value;

        public async Task SearchAsync(string Query, int PageNumber)
        {
            if (IsSearching || string.IsNullOrEmpty(Query) || (SearchResult != null && SearchResult.Query == Query && SearchResult.PageNumber == PageNumber))
                return;

            try
            {
                IsSearching = true;
                ProcessingText = "Searching";

                if (RecentSearches.TryGetValue(GetUniqueSearchId(Query, PageNumber), out SearchResult CachedResults))
                {
                    SearchResult = CachedResults;
                }
                else
                {
                    LiteroticaSearchResults Data = await LiteroticaUtils.SearchStories(Query, PageNumber);
                    SearchResult = new(this, Query, PageNumber, Data);
                }
            }
            catch (Exception ex) { MessageBox.Show(ex.ToString()); }
            finally
            {
                IsSearching = false;
                ProcessingText = null;
            }
        }

        private bool _IsOpeningStory;
        public bool IsOpeningStory
        {
            get => _IsOpeningStory;
            private set
            {
                if (_IsOpeningStory != value)
                {
                    _IsOpeningStory = value;
                    NPC(nameof(IsOpeningStory));
                }
            }
        }

        /// <summary>
        /// Key = the http-encoded title of a story chapter, such as "accidents-happen-1" (<see href="https://www.literotica.com/s/accidents-happen-1"/>)<br/>
        /// Value = the story object that was downloaded for that chapter title<para/>
        /// Since the story objects contain all pages of all chapters, there may be several keys that map to the same story.
        /// </summary>
        private static readonly Dictionary<string, LiteroticaStory> StoriesByChapterTitle = new();
        private bool TryGetStory(string ChapterTitle, out LiteroticaStory Story) => MVM.TryGetStory(ChapterTitle, out Story) || StoriesByChapterTitle.TryGetValue(ChapterTitle, out Story);

        public DelegateCommand<LiteroticaSubmission> OpenStory => new(async (LiteroticaSubmission Submission) =>
        {
            if (IsOpeningStory)
                return;

            try
            {
                IsOpeningStory = true;

                string StoryUrl = Submission.fullUrl;
                if (!LiteroticaUtils.TryGetAPIUri(StoryUrl, out _))
                {
                    if (!string.IsNullOrEmpty(StoryUrl))
                        MessageBox.Show($"Invalid literotica story url format: {StoryUrl}");
                    return;
                }

                ProcessingText = $"Downloading and opening story:\n{StoryUrl}";

                if (!LiteroticaUtils.TryGetStoryTitle(StoryUrl, out string ChapterTitle) || !TryGetStory(ChapterTitle, out LiteroticaStory StoryVM))
                {
                    SerializableStory Story = await LiteroticaUtils.DownloadStory(StoryUrl);
                    StoryVM = new(MVM, Story, null);

                    //  Cache the story so it won't be downloaded again during this session
                    foreach (SerializableChapter Chapter in Story.Chapters)
                        StoriesByChapterTitle[Chapter.Url] = StoryVM;
                }

                await MVM.SetSelectedStoryAsync(StoryVM, true, true);
            }
            catch (Exception ex) { MessageBox.Show(ex.ToString()); }
            finally
            {
                IsOpeningStory = false;
                ProcessingText = null;
            }
        });

